$(function(){$(".post h2, .post h3").each(function(){var e=$(this).text().toLowerCase().replace(/[^a-z0-9]+/g,"_"),t=$(this).parent().parent().attr("id");$(this).attr("id",e+"_"+t)});$('.post [data-role="possum"]').each(function(){$(this).appendTo($(this).parent().parent().parent().siblings(".possum-bar"));var e=$(this).parent().parent().find("#"+$(this).attr("id")),t=$(e).position();$(this).css("top",t.top)})});